const initialState = {
  count: 0,
};

export default (state = initialState, action) => {
  switch (action.type) {
    case "INCREMENT":
      return {
        ...state,
        count: state + action.score,
      };
    case "DECREMENT":
      return state - action.score;
    default:
      return state;
  }
};
