import React from "react";
import { connect, useSelector } from "react-redux";
import { increment, decrement } from "./reducers/actions";

const User = () => {
  const GG = useSelector((state) => state);
  console.log("GG -> ", GG);
};

const App = ({ message, counter, dispatch }) => {
  return (
    <div className="container">
      <div className="columns column is-12">
        <h1>Counter : {counter}</h1>
      </div>
      <div className="buttons">
        <button
          onClick={() => dispatch(increment(1))}
          className="button is-primary"
        >
          +1
        </button>
        <button
          onClick={() => dispatch(increment(2))}
          className="button is-link"
        >
          +2
        </button>
        <button
          onClick={() => dispatch(increment(3))}
          className="button is-info"
        >
          +3
        </button>
      </div>
      <div className="buttons">
        <button
          onClick={() => dispatch(decrement(1))}
          className="button is-primary"
        >
          -1
        </button>
        <button
          onClick={() => dispatch(decrement(2))}
          className="button is-link"
        >
          -2
        </button>
        <button
          onClick={() => dispatch(decrement(3))}
          className="button is-info"
        >
          -3
        </button>
      </div>
    </div>
  );
};
const mapStateToProps = function (state) {
  return {
    message: "This is message from mapStateToProps",
    counter: state.counters || 0,
  };
};
const AppWithConnect = connect(mapStateToProps)(App);
export default AppWithConnect;
